import React from "react";
import { useState } from "react";


function RegistrationForm(){
    const [hideRec,setHiddenRecords] =useState({
        isHide:true
    });

    const [records, setRecords] = useState({
        userName : '',
        Email :'',
        phoneNumber:'',
        addressData:''
    });

    const getMyFormData = (e) =>{    
        debugger;  
      let name=e.target.name;
       let value=e.target.value;
       setRecords({...records,[name]:value})

    }

    const submitFormData = (e) => {
        debugger;
       e.preventDefault();
       let isHide=false;
       setHiddenRecords({...hideRec,[isHide]:false})
       console.log(hideRec);
    }

   return (
    <>
    <form action="" >
        <div>
            <label>FirstName</label>
            <input type="text" value={records.userName} id="userName"  onChange={getMyFormData} name="userName"></input>
        </div>
        <div>
            <label>Email</label>
            <input type="text" value={records.Email} id="Email"  onChange={getMyFormData} name="Email"></input>
        </div>
        <div>
            <label>Phone Number</label>
            <input type="text" value={records.phoneNumber} id="phoneNumber"  onChange={getMyFormData} name="phoneNumber"></input>
        </div>
        <div>
            <label>Address</label>
            <input type="text" value={records.addressData} id="addressData"  onChange={getMyFormData} name="addressData"></input>
        </div>
        <br>
        </br>
        <div>
            <button  id="mySubmitBtn" onClick={submitFormData}>Submit</button>

            <br></br>

            <div hidden={hideRec.isHide}>
                <span>{records.userName}</span>  <br></br>
                <span>{records.Email}</span>  <br></br>
                <span>{records.phoneNumber}</span>  <br></br>
                <span>{records.addressData}</span>  <br></br>
            </div>

        </div>
    </form>
    </>
   )
}

export default RegistrationForm
